jQuery(document).ready(function($){"use strict";const copyButtonLabel="Copy Code";const sleep=ms=>new Promise(r=>setTimeout(r,ms));const addHeaderToCodeBlocks=async()=>{await sleep(2e3);const blocks=document.querySelectorAll("div.wp-block-syntaxhighlighter-code ");blocks.forEach(block=>{const header=document.createElement("header");const span=document.createElement("span");block.insertBefore(header,block.childNodes[0]);header.appendChild(span);if(navigator.clipboard){const button=document.createElement("button");button.innerText=copyButtonLabel;button.classList.add("copy-to-clipboard");button.addEventListener("click",async()=>{await copyCode(block,button)});header.appendChild(button)}})};const copyCode=async(block,button)=>{let code=block.querySelector("td.code div.container");let text=code.innerText;await navigator.clipboard.writeText(text);button.innerText="Code Copied";setTimeout(()=>{button.innerText=copyButtonLabel},700)};addHeaderToCodeBlocks()});